import React from 'react';

const Result = () => {

    function get_cookie(Name) {
        var search = Name + "="
        var returnvalue = "";
      
        if (document.cookie.length > 0) {
          offset = document.cookie.indexOf(search)
      
          if (offset != -1) {
            offset += search.length
            end = document.cookie.indexOf(";", offset);
            if (end == -1) end = document.cookie.length;
            returnvalue=unescape(document.cookie.substring(offset, end))
          }
      
        }
        return returnvalue;
      }
      
       
      
      function oneload() {
        var cookiename=window.location.pathname
      
        var flag = eval(get_cookie(window.location.pathname));
      
      
        if(flag || flag == null) {
      
          var cookievalue="false;"
          document.cookie=cookiename+"="+cookievalue;
      
          location.reload();
          alert("새로 고침");  //새로고침이 1회만 실행되는지 확인하시고 지우세요.
        }
        else {
          var cookievalue="true;"
          document.cookie=cookiename+"="+cookievalue;
      
        }
      }
   
    return (
    <div className="main_home">

        <p className="hell-met">Hell-met</p>
        <img src="http://localhost:8000/media/out_images/face.png" width='350'></img>
         {onLoad="oneload()"}
        <br></br>
    </div>
    );
};

export default Result;
