import React, { useRef, useState, useEffect } from 'react';
import './WebcamCapture.css';
import Webcam from "react-webcam"
import { Link } from 'react-router-dom';
import imglogo from './helmet_example1.png';

const WebcamComponent = () => <Webcam />;

const videoConstraints = {
    width: 500,
    height: 500,
    facingMode: "user"
};

const WebcamCapture = () => {
   const [image,setImage]=useState('');
    const webcamRef = React.useRef(null);

    const capture = React.useCallback(
        () => {
        const imageSrc = webcamRef.current.getScreenshot();
        setImage(imageSrc)
        });

    const handleUpload = () => {
        const data = new FormData();
        data.append('imageSrc',this.imageSrc)

        fetch('http://localhost:8000/api/upload',{
            method: 'POST',
            body: data
        })
    }

    return (
        <div className="webcam-container">
            <div className="box-left">
                    <div className="webcam-img-left">
                        {image == '' ? <Webcam
                        audio={false}
                        height={500}
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        width={500}
                        videoConstraints={videoConstraints}
                        /> : <img src={image} />}
                    </div>
                    <br></br>
                    <div>
                        {image != '' ?
                        <button onClick={(e) => {
                        e.preventDefault();
                        setImage('')
                        }}
                        className="webcam-btn">Again </button> :
                        <button onClick={(e) => {
                        e.preventDefault();
                        capture();
                        }}
                        className="webcam-btn">Capture</button>
                        }
                    </div>
            </div>


            <div className="box-right">
                    <div className="webcam-img-right">
                    {image == '' ? <img
                        src={imglogo}
                        height={500}
                        width={500}
                        /> : <img src={image} />}
                    </div>
                    <br></br>
                    <br></br>
                    <Link className="check-btn" to="./" onClick={() => this.handleUpload()} >Check !</Link>
            </div>
        </div>
    );
}

export default WebcamCapture;
